import React, { useState, useRef, useEffect } from 'react';
import './App.css';

const examplePrompts = [
  'How do I grow palm oil sustainably?',
  'What fertilizer should I use this season?',
  'Recommend tools for harvesting.'
];

const productRecommendations = [
  { name: 'Organic Fertilizer A', image: 'https://via.placeholder.com/100', price: 'IDR 150,000' },
  { name: 'Palm Sickle Tool', image: 'https://via.placeholder.com/100', price: 'IDR 90,000' },
  { name: 'Eco Mulch Pack', image: 'https://via.placeholder.com/100', price: 'IDR 200,000' }
];

function App() {
  const [messages, setMessages] = useState([
    { text: 'üëã Hello! I‚Äôm PalmPal. How can I help your farm today?', sender: 'bot' }
  ]);
  const [input, setInput] = useState('');
  const [language, setLanguage] = useState('en');
  const messagesEndRef = useRef(null);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { text: input, sender: 'user' };
    setMessages((prev) => [...prev, userMessage]);
    setInput('');

    try {
      const response = await fetch('http://localhost:5000/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: input, language })
      });
      const data = await response.json();

      const botMessage = { text: data.reply, sender: 'bot' };
      setMessages((prev) => [...prev, botMessage]);
    } catch (err) {
      setMessages((prev) => [...prev, { text: '‚ö†Ô∏è Something went wrong.', sender: 'bot' }]);
    }
  };

  const handlePromptClick = (prompt) => setInput(prompt);
  const handleKeyPress = (e) => e.key === 'Enter' && sendMessage();
  const toggleLanguage = () => setLanguage((prev) => (prev === 'en' ? 'id' : 'en'));

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  return (
    <div className="chat-container">
      <div className="header">
        üå¥ PalmPal Chatbot <button onClick={toggleLanguage}>üåê {language === 'en' ? 'EN' : 'ID'}</button>
      </div>

      <div className="example-prompts">
        {examplePrompts.map((prompt, i) => (
          <button key={i} onClick={() => handlePromptClick(prompt)}>{prompt}</button>
        ))}
      </div>

      <div className="chat-box">
        {messages.map((msg, idx) => (
          <div
            key={idx}
            className={`chat-bubble ${msg.sender === 'user' ? 'user' : 'bot'}`}
          >
            {msg.text}
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      <div className="recommendation-section">
        <h4>üõçÔ∏è Recommended Products</h4>
        <div className="products">
          {productRecommendations.map((item, i) => (
            <div className="product-card" key={i}>
              <img src={item.image} alt={item.name} />
              <div>{item.name}</div>
              <small>{item.price}</small>
            </div>
          ))}
        </div>
      </div>

      <div className="input-bar">
        <input
          type="text"
          placeholder="Ask something..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyPress}
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
}

export default App;
